void HEARTBEAT_loop0(
  uint64_t maxIter,
  double* __restrict__ A_vals
) {
  for (uint64_t pA = 0; pA < maxIter; pA++) {
    A_vals[pA] = 0.0;
  }
}

void HEARTBEAT_loop4(
  uint64_t maxIter,
  int A2_dimension,
  double* __restrict__ A_vals,
  double* __restrict__ B_vals,
  int C2_dimension,
  double* __restrict__ C_vals,
  int D2_dimension,
  double* __restrict__ D_vals,
  uint64_t i,
  uint64_t k,
  uint64_t l,
  uint64_t lB
) {
  for (uint64_t j = 0; j < maxIter; j++) {
    uint64_t jA = i * A2_dimension + j;
    uint64_t jC = k * C2_dimension + j;
    uint64_t jD = l * D2_dimension + j;
    A_vals[jA] = A_vals[jA] + (B_vals[lB] * C_vals[jC]) * D_vals[jD];
  }
}

void HEARTBEAT_loop3(
  uint64_t startIter,
  uint64_t maxIter,
  int A2_dimension,
  double* __restrict__ A_vals,
  int* __restrict__ B3_crd,
  double* __restrict__ B_vals,
  int C2_dimension,
  double* __restrict__ C_vals,
  int D2_dimension,
  double* __restrict__ D_vals,
  uint64_t i,
  uint64_t k
) {
  for (uint64_t lB = startIter; lB < maxIter; lB++) {
    uint64_t l = B3_crd[lB];
    HEARTBEAT_loop4(D2_dimension, A2_dimension, A_vals, B_vals, C2_dimension, C_vals, D2_dimension, D_vals, i, k, l, lB);
  }
}

void HEARTBEAT_loop2(
  uint64_t startIter,
  uint64_t maxIter,
  int A2_dimension,
  double* __restrict__ A_vals,
  int* __restrict__ B2_crd,
  int* __restrict__ B3_pos,
  int* __restrict__ B3_crd,
  double* __restrict__ B_vals,
  int C2_dimension,
  double* __restrict__ C_vals,
  int D2_dimension,
  double* __restrict__ D_vals,
  uint64_t i
) {
  for (uint64_t kB = startIter; kB < maxIter; kB++) {
    uint64_t k = B2_crd[kB];
    HEARTBEAT_loop3(B3_pos[kB], B3_pos[(kB + 1)], A2_dimension, A_vals, B3_crd, B_vals, C2_dimension, C_vals, D2_dimension, D_vals, i, k);
  }
}

void HEARTBEAT_loop1(
  uint64_t maxIter,
  int A2_dimension,
  double* __restrict__ A_vals,
  int* __restrict__ B2_pos,
  int* __restrict__ B2_crd,
  int* __restrict__ B3_pos,
  int* __restrict__ B3_crd,
  double* __restrict__ B_vals,
  int C2_dimension,
  double* __restrict__ C_vals,
  int D2_dimension,
  double* __restrict__ D_vals
) {
  for (uint64_t i = 0; i < maxIter; i++) {
    HEARTBEAT_loop2(B2_pos[i], B2_pos[(i + 1)], A2_dimension, A_vals, B2_crd, B3_pos, B3_crd, B_vals, C2_dimension, C_vals, D2_dimension, D_vals, i);
  }
}

void MTTKRP_hbc(
  int A1_dimension,
  int A2_dimension,
  double* __restrict__ A_vals,
  int B1_dimension,
  int* __restrict__ B2_pos,
  int* __restrict__ B2_crd,
  int* __restrict__ B3_pos,
  int* __restrict__ B3_crd,
  double* __restrict__ B_vals,
  int C2_dimension,
  double* __restrict__ C_vals,
  int D2_dimension,
  double* __restrict__ D_vals
) {
  HEARTBEAT_loop0((A1_dimension * A2_dimension), A_vals);
  HEARTBEAT_loop1(B1_dimension, A2_dimension, A_vals, B2_pos, B2_crd, B3_pos, B3_crd, B_vals, C2_dimension, C_vals, D2_dimension, D_vals);

}

